import React, { useState, useCallback, useEffect } from "react";
import { map, evolve } from "ramda";
import { initialTransmitters, initialPos } from "./initialData";

import Viewer from "./Viewer";
import WifiIcon from "@mui/icons-material/Wifi";
import SensorDetails from "./SensorDetails";

const INITIAL_MODE = "3d";

export default function AddDataElements() {
  const [space, setSpace] = useState();
  const [mode, setMode] = useState(INITIAL_MODE);

  const [ssid, setSsid] = useState("");

  const [transmitters, setTransmitters] = useState([initialTransmitters]);

  // fetch most recent data for each transmitter
  const fetchTransmitters = async () => {
    const newData = [];
    for (let i = 1; i <= 5; i++) {
      const data = await fetch("/transmitter/" + i);
      const items = await data.json();
      if(items[0]) {
        // if there's an item, put it into the newData
        newData.push(items[0]);
      } else {
        // if there isn't, use initial value
        newData.push(initialTransmitters[i-1]);
      }
    }
    // console.log(newData);
    setTransmitters(newData);
  };

  function handleSetSsid(event) {
    setSsid(event.target.value);
  }

  // set rssi for each transmitter
  function handleSsidSubmit() {
    let newRssiArray = [...transmitterPos];
    let wantedSsid = ssid;

    newRssiArray.map((newRssi, index) => {
      const foundArr = transmitters[index].network_array.find((element) => {
        return element.ssid === wantedSsid;
      });
      if(foundArr) {
        return newRssi.rssi = foundArr.rssi; 
      } else {
        return null;
      }
    });
  }

  const [transmitterPos, setTransmitterPos] = useState(initialPos);

  function handleUpdatePosition(transmitter_index, coordinates) {
    let newPos = [...transmitterPos];
    newPos[transmitter_index].position = coordinates;
    setTransmitterPos(newPos);
  }

  // memoize so Viewer render once only (wrapped in memo)
  const onReady = useCallback((space) => setSpace(space), []);

  const onModeChange = useCallback(setMode, []);

  const noElevationIn2D = useCallback(
    (value) => (mode === "3d" ? value : 0),
    [mode]
  );
  const autoElevation = map(
    evolve({ position: { elevation: noElevationIn2D } })
  );

  // fetch transmitter data
  useEffect(() => {
    fetchTransmitters();
  }, []);

  // render elements
  useEffect(() => {
    if (!space) {
      return;
    }

    space.addDataLayer({
      id: "transmitter",
      type: "point",
      data: autoElevation(transmitterPos),
      diameter: 1,
      anchor: "bottom",
      color: (d) =>
        d.rssi >= -70
          ? "#2ECC71"
          : d.rssi < -70 && d.rssi >= -85
          ? "#F4D03F"
          : d.rssi <= -86 && d.rssi > -100
          ? "#F39C12"
          : d.rssi <= -100
          ? "#FA1D0F"
          : "undefined",

      tooltip: (d) => {
        return ("Transmitter-" + d.transmitterId + "\n" + "RSSI: " + d.rssi);
      },
    });
  }, [space, transmitterPos, autoElevation]);

  return (
    <div className="container-fluid measure-container">
      <div className="row viewer-row">
        <div className="col-lg-7">
          <Viewer
            mode={INITIAL_MODE}
            onReady={onReady}
            onModeChange={onModeChange}
            spaceID={"78b842f9-0416-43b3-9496-9a6668ab73ad"}
            clientToken={"pub_eb760fee77634cdab2fe31146fc371c2"}
          />
        </div>
        <div className="col-lg-5">
          <div>
            {!space && (
              <div className="start-viewer-heading-div">
                <h2 className="start-viewer-heading">
                  Please start the viewer before measuring.
                  <br />
                  👈
                </h2>
              </div>
            )}
            {space && (
              <div className="container">
                <h3>Enter network SSID:</h3>
                <input
                  onChange={handleSetSsid}
                  type="text"
                  placeholder="my-ssid"
                  value={ssid}
                />
                <button onClick={handleSsidSubmit}>Add</button>

                <h3>Set transmitter position:</h3>
                {transmitterPos.map((tPos, index) => {
                  return (
                    <div className="container">
                      <div className="row add-transmitter">
                        <div className="col" style={{ margin: "auto" }}>
                          <h5>Transmitter-{tPos.transmitterId}</h5>
                        </div>
                        <div className="col" style={{ margin: "auto" }}>
                          <div>
                            <WifiIcon fontSize="large" />
                          </div>
                        </div>
                        <div className="col" style={{ margin: "auto" }}>
                          <h6>RSSI:</h6>
                          <p>{tPos.rssi}</p>
                        </div>
                        <div className="col" style={{ margin: "auto" }}>
                          <button
                            onClick={() => {
                              space.enablePickingMode({
                                onPick: ({ coordinates }) => {
                                  handleUpdatePosition(index, coordinates);
                                  space.disablePickingMode();
                                },
                              });
                            }}
                          >
                            Set position
                          </button>
                        </div>
                      </div>
                    </div>
                  );
                })}
              </div>
            )}
          </div>
        </div>
      </div>
      {/* <SensorDetails transmitters={transmitters}/> */}
      <div>
      <div className="row">
        <div className="col-lg-12 details-heading-col">
          <div className="details-heading-div">
            <h2 className="details-heading">Sensor details 👇:</h2>
          </div>
        </div>
      </div>
      <div className="row">
        {transmitters.map((transmitter, index) => {
          return (
            <Fragment key={index}>
              <div className="col transmitter-col">
                <h3>Transmitter-{transmitter.device_index}</h3>
                <span>
                  Found networks:
                  <br />
                  {transmitter.network_amont}
                </span>
                <div className="network-list">
                {/* {console.log(transmitter)} */}
                  {transmitter.network_array.map((network, _index) => {
                    return (
                      <Fragment key={_index}>
                        <div className="network">
                          <hr />
                          <div className="row row-cols-2">
                            <div className="col">
                              <h5
                                style={{
                                  color:
                                    network.rssi >= -70
                                      ? "green"
                                      : network.rssi < -70 &&
                                        network.rssi >= -85
                                      ? "gold"
                                      : network.rssi < -86 &&
                                        network.rssi > -100
                                      ? "orange"
                                      : network.rssi <= -100
                                      ? "red"
                                      : "undefined",
                                }}
                              >
                                SSID: {network.ssid} <br />
                                RSSI: {network.rssi}
                              </h5>
                            </div>
                            <div className="col">
                              <div style={{ textAlign: "center" }}>
                                <h4
                                  style={{
                                    color:
                                      network.rssi >= -70
                                        ? "green"
                                        : network.rssi < -70 &&
                                          network.rssi >= -85
                                        ? "gold"
                                        : network.rssi < -86 &&
                                          network.rssi > -100
                                        ? "orange"
                                        : network.rssi <= -100
                                        ? "red"
                                        : "undefined",
                                  }}
                                >
                                  <WifiIcon fontSize="large" />{" "}
                                  {network.rssi >= -70
                                    ? "Excellent"
                                    : network.rssi < -70 && network.rssi >= -85
                                    ? "Good"
                                    : network.rssi < -86 && network.rssi > -100
                                    ? "Fair"
                                    : network.rssi <= -100
                                    ? "Poor"
                                    : "undefined"}
                                </h4>
                              </div>
                            </div>
                          </div>
                        </div>
                      </Fragment>
                    );
                  })}
                </div>
              </div>
            </Fragment>
          );
        })}
      </div>
    </div>
    </div>
  );
}
